package Beyblade;

public class Draciel extends BeybladeApp {
	private String kutsalCanavar;

	public Draciel(String beybladeci, int donusHýzý, int saldiriGucu, String kutsalCanavar) {
		super(beybladeci, donusHýzý, saldiriGucu);
		this.kutsalCanavar = kutsalCanavar;
	}

	@Override
	public void bilgileriGoster() {
		// TODO Auto-generated method stub
		super.bilgileriGoster();
		System.out.println("Kutsal canavar Adý : " + kutsalCanavar);
	}

	@Override
	public void kutsalCanavarOrtayaCikar() {
		// TODO Auto-generated method stub
		System.out.println(getBeybladeci() + " " + kutsalCanavar + " ý ortaya çýkarýyor...");
		System.out.println(getBeybladeci() + " ýn Saldýrýsý : Kale Savunmasý");
	}
	
	

}
